import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import './Dashboard.css';

const Dashboard = () => {
  const { user, logout } = useAuth();
  const [projects, setProjects] = useState([]);
  const [showProjectForm, setShowProjectForm] = useState(false);
  const [newProject, setNewProject] = useState({ name: '', description: '' });

  // Demo projects data - replace with actual API calls
  const demoProjects = [
    { id: 1, name: 'Website Redesign', description: 'Redesign company website', createdAt: new Date() },
    { id: 2, name: 'Mobile App', description: 'Develop new mobile application', createdAt: new Date() },
    { id: 3, name: 'Marketing Campaign', description: 'Q4 marketing campaign planning', createdAt: new Date() }
  ];

  useEffect(() => {
    // Simulate API call to fetch projects
    setProjects(demoProjects);
  }, []);

  const handleCreateProject = (e) => {
    e.preventDefault();
    if (newProject.name.trim()) {
      const project = {
        id: projects.length + 1,
        name: newProject.name,
        description: newProject.description,
        createdAt: new Date()
      };
      setProjects([...projects, project]);
      setNewProject({ name: '', description: '' });
      setShowProjectForm(false);
    }
  };

  const handleDeleteProject = (projectId) => {
    setProjects(projects.filter(project => project.id !== projectId));
  };

  return (
    <div className="dashboard-container">
      {/* Header */}
      <header className="dashboard-header">
        <div className="header-content">
          <h1>TaskBoard Pro</h1>
          <div className="user-info">
            <span>Welcome, {user?.name}</span>
            <button onClick={logout} className="logout-btn">
              Logout
            </button>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="dashboard-main">
        <div className="dashboard-actions">
          <button 
            onClick={() => setShowProjectForm(true)}
            className="btn-primary"
          >
            + Create New Project
          </button>
        </div>

        {/* Create Project Form */}
        {showProjectForm && (
          <div className="modal-overlay">
            <div className="modal-content">
              <h3>Create New Project</h3>
              <form onSubmit={handleCreateProject}>
                <div className="form-group">
                  <label>Project Name</label>
                  <input
                    type="text"
                    value={newProject.name}
                    onChange={(e) => setNewProject({...newProject, name: e.target.value})}
                    placeholder="Enter project name"
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Description</label>
                  <textarea
                    value={newProject.description}
                    onChange={(e) => setNewProject({...newProject, description: e.target.value})}
                    placeholder="Enter project description"
                    rows="3"
                  />
                </div>
                <div className="form-actions">
                  <button type="submit" className="btn-success">
                    Create Project
                  </button>
                  <button 
                    type="button" 
                    onClick={() => setShowProjectForm(false)}
                    className="btn-secondary"
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Projects Grid */}
        <div className="projects-grid">
          <h2>Your Projects</h2>
          {projects.length === 0 ? (
            <div className="empty-state">
              <p>No projects yet. Create your first project to get started!</p>
            </div>
          ) : (
            <div className="projects-list">
              {projects.map(project => (
                <div key={project.id} className="project-card">
                  <div className="project-header">
                    <h3>{project.name}</h3>
                    <button 
                      onClick={() => handleDeleteProject(project.id)}
                      className="delete-btn"
                    >
                      Ã—
                    </button>
                  </div>
                  <p className="project-description">{project.description}</p>
                  <div className="project-footer">
                    <span className="project-date">
                      Created: {project.createdAt.toLocaleDateString()}
                    </span>
                    <button className="btn-outline">
                      View Tasks
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
